function unique (arr) {

    var result = []
    var isRepeat

    for (var i = 0; i < arr.length; i++) {

        isRepeat = false  // push
        for (var j = 0; j < result.length; j++) {
            if (arr[i] === result[j]) {
                isRepeat = true
                break
            }
        }

        if (!isRepeat) {
            result.push(arr[i])
        }
    }

    return result

}
unique([1,1,2,3,4])
