function unique (arr) {
    // hashObjhashresult
    var hashObj = {}, result = []
    for (var i = 0; i < arr.length; i++) {
        // hash
        if (!hashObj[arr[i]]) {
            // hash
            hashObj[arr[i]] = true
            // push
            result.push(arr[i])
        }
    }
    return result
}
