})

export default {
    inserted(el, binding) {

        // 
        el.src = 'http://localhost:8080/static/images/before_load.png'
        // lazy-src
        el.lazySrc = binding.value
        // 
        observer.observe(el)
    },
    unbind() {
        // 
        observer.disconnect()
    }
}
